func (s *Service) ProcessData(timeoutCtx context.Context, r io.Reader) error {
  errCh := make(chan error)

  go func() {
    errCh <- s.processDataInternal(r)
  }()


  select {
  case err := <-errCh:
    return err
  case <-timeoutCtx.Done():
    return timeoutCtx.Err()
  }
}

